\documentclass[12pt]{article} \usepackage{fullpage} \emergencystretch=3em \usepackage[utf8]{inputenc} \usepackage{amssymb} \usepackage{amsmath} \makeatletter enewcommand{	ag}[1]{} \makeatother \usepackage{amsthm} \usepackage{newunicodechar} \usepackage[usenames]{color} \usepackage[colorlinks=true,linkcolor=webgreen,filecolor=webbrown,citecolor=webgreen]{hyperref} \definecolor{webgreen}{rgb}{0,.5,0} \definecolor{webbrown}{rgb}{.6,0,0} \providecommand{\seqnum}[1]{#1} 
